mapper.select:
select user_id, attendance_date, count
from attendance
where user_id = #{userId}
and
attendance_date = #{attendanceDate}

mapper.update:
update attendance
set count = #{attVo.count}
where user_id = #{userId}
and
attendance_date = #{attVo.attendanceDate}

mapper.select:
  		select count(*)
		from attendance
		where attendance_date = #{attendanceDate}
