mapper.update:
update comments
set contents = #{commentVo.contents},
	modify_time = #{commentVo.modifyTime}
where id = #{commentVo.id}

update comments
	set delete_time = #{commentVo.deleteTime}
where id = #{commentVo.id}

update comments
	set delete_time = #{commentVo.deleteTime}
where user_nickname = #{commentVo.userNickName}
